mapper.select:
select * from community
where (community_creat_etime between #{startDate} and #{endDate}) and community_is_delete = 0
  limit #{limit}, #{offset}

mapper.update:
update community
community_name = #{community.communityName} , community_address = #{community.communityAddress} , community_area = #{community.communityArea} , community_build = #{community.communityBuild}, Community_greening = #{community.communityGreening}, community_icon = #{community.communityIcon}, community_developers = #{community.communityDevelopers}, community_company = #{community.communityCompany}, community_creat_etime = #{community.communityCreatEtime}, community_status = #{community.communityStatus}, community_is_delete = #{community.communityIsDelete},
where community_id = #{community.communityId}
