mapper.select:
select pet_id, pet_owner_id, pet_name, pet_color, pet_adoptiontime, pet_photo, pet_describe, owner_name from pet p left join owner o on p.pet_owner_id = o.owner_id
pet_status = 0
and ( owner_name like concat('%',#{searchDto.keyword},'%') or pet_color like concat('%',#{searchDto.keyword},'%') or pet_name like concat('%',#{searchDto.keyword},'%') )
and pet_adoptiontime between #{searchDto.startDate} and #{searchDto.endData}
limit #{searchDto.page},#{searchDto.size}

mapper.update:
update pet set  pet_status = 1,pet_updatetime = now()
where pet_status = 0 and pet_id in

    #{petId}
