mapper.select:
select vehicle_id, vehicle_owner_id, vehicle_number, vehicle_color, vehicle_photo, vehicle_describe, vehicle_createtime, vehicle_updatetime, vehicle_status, owner_name from vehicle v left join owner o on v.vehicle_owner_id = o.owner_id
vehicle_status = 0
and ( owner_name like concat('%',#{searchDto.keyword},'%') or vehicle_color like concat('%',#{searchDto.keyword},'%') or vehicle_number like concat('%',#{searchDto.keyword},'%') )
and v.vehicle_createtime between #{searchDto.startDate} and #{searchDto.endData}
limit #{searchDto.page},#{searchDto.size}

mapper.update:
update vehicle set  vehicle_status = 1,vehicle_updatetime = now()
where vehicle_status = 0 and vehicle_id in

   #{vehicleId}
